import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const MostVotes = ({votes}) => {
  const index = votes.indexOf(Math.max(...votes))
  return (
  <div>
    <h1>Anecdote with most votes</h1>
      {anecdotes[index]} <b>{votes[index]} votes</b>
  </div>
)}

const App = ({anecdotes}) => {
  const [selected, setSelected] = useState(0)
  const[votes, setVotes] = useState(Array(6).fill(0))
  const random = () => Math.floor(Math.random() * anecdotes.length)
  const vote = () => {
    const copy = [...votes]
    copy[selected] += 1
    setVotes(copy)
  }
  return (
    <div>
      {anecdotes[selected]} <b>{votes[selected]} votes</b><br />
      <button onClick={vote}>vote</button>
      <button onClick={() => setSelected(random)}>next anecdote</button>
      <MostVotes votes={votes} />
    </div>

  )
}

const anecdotes = [
  'If it hurts, do it more often',
  'Adding manpower to a late software project makes it later!',
  'The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.',
  'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
  'Premature optimization is the root of all evil.',
  'Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.'
]

ReactDOM.render(
  <App anecdotes={anecdotes} />,
  document.getElementById('root')
)
